INSERT INTO `wp_options` VALUES("2902", "_transient_dash_4077549d03da2e451c8b5f002294ff51", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2015/05/wordpress-4-2-2/\'>WordPress 4.2.2 Security and Maintenance Release</a> <span class=\"rss-date\">May 7, 2015</span><div class=\"rssSummary\">WordPress 4.2.2 is now available. This is a critical security release for all previous versions and we strongly encourage you to update your sites immediately. Version 4.2.2 addresses two security issues: The Genericons icon font package, which is used in a number of popular themes and plugins, contained an HTML file vulnerable to a cross-site [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://www.manton.org/2015/06/more-quick-blogging-workflows.html\'>Alex King: More micro-blogging workflows</a></li><li><a class=\'rsswidget\' href=\'http://ma.tt/2015/06/fight-against-uber/\'>Matt: Fight Against Uber</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/announcing-wordcamp-europe-2016/\'>Post Status: Announcing: WordCamp Europe 2016</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/ewww-image-optimizer/\' class=\'dashboard-news-plugin-link\'>EWWW Image Optimizer</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=ewww-image-optimizer&amp;_wpnonce=7e71e5dc8b&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'EWWW Image Optimizer\'>Install</a>)</span></li></ul></div>", "no");
